mapper.select:
select read_auth
from company_member
WHERE user_no = #{userNo}
  AND company_no = #{companyNo}

select edit_auth
from company_member
WHERE user_no = #{userNo}
  AND company_no = #{companyNo}

select role
from company_member
WHERE user_no = #{userNo}
  AND company_no = #{companyNo}

select company_no = #{company_no}
from company_member
WHERE no = #{member_no}
