mapper.select:
select EXISTS(select *
              from relation
              WHERE database_no = #{database_no}
                AND main_table = #{main_table}
                AND main_column = #{main_column}
                AND target_table = #{target_table}
                AND target_column = #{target_column})

mapper.delete:
delete
from relation
WHERE database_no = #{database_no}
  AND main_table = #{main_table}
  AND main_column = #{main_column}
  AND target_table = #{target_table}
  AND target_column = #{target_column}
