mapper.select:
select *, JSON_OBJECT('top', top, 'left', `left`) as position
from `table`
WHERE database_no = #{database_no}

mapper.update:
update `table`
SET name = #{name}
WHERE id = #{id}
  AND database_no = #{database_no}

update `table`
SET top    = #{position.top},
    `left` = #{position.left}
WHERE id = #{table_id}
  AND database_no = #{database_no}

mapper.delete:
delete
from `table`
WHERE database_no = #{database_no}
  AND id = #{table_id}
