mapper.select:
select *
from user
WHERE email = #{email}
  AND aes_decrypt(unhex(password), 'okiwi') = #{password}

select email
from user
WHERE phone = #{phone}

mapper.update:
update user
SET password = hex(aes_encrypt(#{password}, 'okiwi'))
WHERE email = #{email}

update user
SET name = #{name}
WHERE no = #{userNo}

update user
SET phone = #{phone}
WHERE no = #{userNo}
